import SingleEvent from "@/components/Events/SingleEvent.vue";
import CheckIn from "@/components/CheckIn.vue";
import TileFormat from "@/components/Formatting/TileFormat.vue";
import SingleWhiskeyRating from "@/components/Whiskey/SingleWhiskeyRating.vue";

export default {
  components: {
    SingleEvent,
    CheckIn,
    TileFormat,
    SingleWhiskeyRating
  },
  name: "WhiskeyRatingList",
  props: {
    eventId: Number,
    apiURLWhiskey: String,
    whiskeyRatingList: Array,
    whiskeyRating: {
      whiskeyId: Number,
      userId: Number,
      eventId: Number,
      tasteRating: Number,
      smellRating: Number,
      colorRating: Number,
      bodyRating: Number,
      finishRating: Number,
      priceRating: Number,
      overallRating: Number
    },
    event: {
      title: String,
      imgUrl: String,
      date: new Date(),
      time: String,
      location: String,
      eventDescription: "",
      isBlindTasting: true,
      eventId: Number
    },
    data() {
      return {
        numOfWhiskeys: "",
        numOfReviewers: "",
        whiskeyList: [],
        whiskeyRatingList: [],
        whiskeyBrandList: [],
        whiskeyReviewerList: [],
        whiskeyApiURL:
          "http://localhost:8080/AuthenticationApplication/api/event/" +
          this.eventId +
          "/whiskeyRating"
      };
    },
    created() {
      this.listWhiskeyRatings();
    },
    methods: {
      listWhiskeyRatings() {
        fetch(this.apiURLWhiskey)
          .then(response => {
            console.log(response);
            return response.json();
          })
          .then(jsonList => {
            this.whiskeyRatingList = jsonList;
            getAveragesOfAllRatings();
            getPercentagesOfAllRatings();
          })
          .catch(err => console.error(err));
      },
      getExistingWhiskeys() {
        fetch(this.apiURL)
          .then(response => {
            return response.json();
          })
          .then(jsonWhiskeys => {
            this.whiskeyList = jsonWhiskeys;
          })
          .catch(err => console.error(err));
      },
      countWhiskey(whiskeyRating) {
        if (!whiskeyBrandList.contains(whiskeyRating.whiskeyId)) {
          this.numOfWhiskeys += 1;
          whiskeyBrandList.push(whiskeyRating.whiskeyId);
        }
      },
      countReviews(whiskeyRating) {
        if (!whiskeyReviewerList.contains(whiskeyRating.userId)) {
          this.numOfReviewers += 1;
          whiskeyReviewerList.push(whiskeyRating.userId);
        }
      }
    }
  }
};
